import React, { useEffect, useState } from "react";
import { useHistory } from 'react-router-dom';
import "./styles.css";
import {fetchChecks, submitCheckResults} from "./api";
import "./Button.css";
import "./FetchCheck.css";
import Button from "./Button";
const FetchCheck = (props) => {
    const [checks, setChecks] = useState(null);
    const [isSubmitDisabled, disableSubmit] = useState(true);
    let results = [], dataArray=[];
    useEffect(() => {
        fetchChecks().then(p => setChecks(p));
    }, []);
    if (checks === null) {
        return <h2>Loading...</h2>;
    }
    return (
        <div className="App">
            {checks.sort((a, b) => a.priority > b.priority ? 1 : -1).map((check, index) => (
                <div  key={check.id}  className={`container ${disableElement(check, index)}`} >
                    <div className="question">{check.description}</div>
                    <div className="tab" value="" id={index}>
                        <button id="yes" tabindex="-1"  value={"Yes"} onClick={handleChange} autofocus="true">Yes</button>
                        <button id="no" tabindex="-1"  value={"No"} onClick={handleChange}>No</button>
                    </div>
                    <br />
                </div>
            ))}
            <br />
            <div class="submit_section"><Button disabled="disabled" id="app_submit" onClick={submitData}>Submit</Button></div>
        </div>
    );
    function disableElement(check,index) {
        dataArray[index]={'id': index,'checkId':check.id, 'value' : null};
        console.log( dataArray);
        return checks[0] === check ? false : "disabled"; 
    }
    function handleChange(e) {
    }
    function submitData(e) {
        e.preventDefault();
        submitCheckResults(results).then(() => {
           // history.push("./success");
        }).catch(() => {
            alert("Submission Failed!!!");
            window.location.reload();
        });
    }
}
  export default FetchCheck;
