import React, { useEffect, useState } from "react";
import { useHistory } from 'react-router-dom';
import "./styles.css";
import {fetchChecks, submitCheckResults} from "./api";
import "./Button.css";
import Button from "./Button";
const FetchCheck = (props) => {
    const [checks, setChecks] = useState(null);
    const [isSubmitDisabled, disableSubmit] = useState(true);
    let results = [];
    useEffect(() => {
        fetchChecks().then(p => setChecks(p));
    }, []);
    if (checks === null) {
        return <h2>Loading...</h2>;
    }
    return (
        <div className="App">
            {checks.sort((a, b) => a.priority > b.priority ? 1 : -1).map((check, index) => (
                <div className="container" id={index} key={check.id} disabled={divDisabled(checks, index)} >
                    <div className="question">{check.description}</div>
                    <div className="tab" name={check.id} id={index} value="">
                        <input type="button" id="yes" name={check.id} value={"Yes"} onClick={handleChange} />
                        <input type="button" id="no" name={check.id} value={"No"} onClick={handleChange} />
                    </div>
                    <br />
                </div>
            ))}
            <br />
            <div><Button disabled="disabled" onClick={submitResults}>Submit</Button></div>
        </div>
    );
function divDisabled(checks, check) {
    return checks[0] === check ? false : true;
  } 
}
  export default FetchCheck;
